mapper.select:
SELECT article_image_id "article_image_id",article_image_extension "article_image_extension", article_id "article_id" 
FROM article_images
WHERE article_id = #{article_id}

SELECT article_id "article_id", article_title "article_title",article_content "article_content", IF((DATEDIFF(NOW(),DATE_FORMAT(article_date,'%Y-%m-%d %T')))	<1,DATE_FORMAT(article_date,'%T'),DATE_FORMAT(article_date,'%Y-%m-%d')) "article_date", article_view "article_view", user_id "user_id", user_name "user_name", IF(user_id=#{user_id},'true','false') AS "editable"
FROM articles NATURAL JOIN users
WHERE article_id = #{article_id}

mapper.update:
UPDATE articles
SET article_view = article_view + 1
WHERE article_id = #{article_id}
